import React from "react";
import styled from "styled-components";
import BarraPrincipal from "../components/NavBar/BarraPrincipal";
import {Submit1} from "../components/Styled";
import {postRequestCer} from "../context/Request";
import {useHistory} from "react-router-dom";


const Gridlogin1 = styled.div`
  grid-row: 2;
  width: 30vw;
  height: 65vh;
  background:#E1F1F9 ;
  
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 70px auto;
  grid-template-areas:
  "a1"
  "a2"
  "a3";
  
  margin-top:20px;
  margin-bottom:20px;
  justify-self: center;
  padding: 70px 30px ;
  border-radius: 20px;
  box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.252);
  
  a{
  text-decoration: none;
  font-size: 12px;
  line-height: 20px;
  color: blue;
  }
  @media(max-width: 900px){
        width:80vw;
        height:500px;
  }
  
  
`;

const Gridlogin = styled.div`
  display:grid;
  grid-template-columns: 1fr;
  grid-templates-rows: 60px auto;
  width:100vw;
  
  

`;
const Form = styled.form`
    grid-area: a2;
    text-align: center;
`;
const B = styled.div`
    grid-area: a3;
`;
const Password1= styled.input`
  width: 100%;
  margin-bottom: 50px;
  border: none;
  border-bottom: 1px solid ;
  background: transparent;
  outline: none;
  height: 40px;
  color: black;
  font-size:16px ;
`;
const A2 = styled.a`
  text-decoration: none;
  font-size: 12px;
  line-height: 20px;
  color: black;
  :hover{
    color:black;
  }
  
`;
const Text1 = styled.input`
  width: 100%;
  margin-bottom: 50px;
  border: none;
  border-bottom: 1px solid ;
  background: transparent;
  outline: none;
  height: 40px;
  color: black;
  font-size:16px ;
`;

const Label1 = styled.label`
  margin: 0;
  padding: 0;
  font-weight: bold;
  display: block;

`;

const H1 = styled.h1`
  margin: 0;
  padding:0 0 20px ;
  text-align: center;
  font-size: 22px;
  grid-area: a1;

`;

const GridBarra = styled.div`
   width:100vw; 
`;

export default function Forgot(){

    const history = useHistory()

    async function handleSubmit(e) {
        e.preventDefault()
        const rut = e.target.rut.value;
        const password1 = e.target.password1.value;
        const password2 = e.target.password2.value;
        const data = {
            rut,
            password1,
            password2
        }

        const resp = await postRequestCer("http://127.0.0.1:8000/api/change-password", JSON.stringify(data))
        if(resp.status === 200){
            alert("Contraseña cambiada con exito")
            history.push("/")
        }else{
            alert("Problemas al cambiar la contraseña, http: " + resp.status)
        }

    }

    return(
        <Gridlogin>
            <GridBarra>
                <BarraPrincipal/>
            </GridBarra>



            <Gridlogin1>
                <H1>Recuperación de cuenta</H1>
                <Form onSubmit={(e) => handleSubmit(e)}>
                    <Label1>Rut de usuario </Label1>
                    <Text1 placeholder ="Ingrese rut usuario" id="rut"></Text1>

                    <Label1>Ingrese la contraseña</Label1>
                    <Password1 type ="password" placeholder="Ingrese nueva contraseña" id="password1"></Password1>

                    <Label1>Repita la contraseña</Label1>
                    <Password1 type ="password" placeholder="Repita la contraseña" id="password2"></Password1>

                    <Submit1 type ="submit" value ="Cambiar contraseña"></Submit1>

                </Form>
                <B>
                    <A2 href ="/">Volver al inicio</A2>
                </B>

            </Gridlogin1>

        </Gridlogin>


    )

}
